// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

/// @title Library for encoding/decoding CBOR for Chainlink Functions
/// @notice https://www.rfc-editor.org/rfc/rfc7049.html
library CBOR {
  uint8 private constant MAJOR_TYPE_INT = 0;
  uint8 private constant MAJOR_TYPE_NEGATIVE_INT = 1;
  uint8 private constant MAJOR_TYPE_BYTES = 2;
  uint8 private constant MAJOR_TYPE_STRING = 3;
  uint8 private constant MAJOR_TYPE_ARRAY = 4;
  uint8 private constant MAJOR_TYPE_MAP = 5;
  uint8 private constant MAJOR_TYPE_TAG = 6;
  uint8 private constant MAJOR_TYPE_CONTENT_FREE = 7;

  uint8 private constant TAG_TYPE_BIGNUM = 2;
  uint8 private constant TAG_TYPE_NEGATIVE_BIGNUM = 3;

  struct CBORBuffer {
    Buffer.buffer buf;
    uint256 depth;
  }

  struct CBORType {
    uint256 len;
    uint256 majorType;
    uint256 additionalInformation;
  }

  uint8 private constant CBOR_TYPE_FALSE = 0x1;
  uint8 private constant CBOR_TYPE_TRUE = 0x2;
  uint8 private constant CBOR_TYPE_NULL = 0x3;
  uint8 private constant CBOR_TYPE_UNDEFINED = 0x4;

  function create(uint256 capacity) internal pure returns (CBORBuffer memory) {
    Buffer.buffer memory buf;
    Buffer.init(buf, capacity);
    CBORBuffer memory cbor = CBORBuffer(buf, 0);
    return cbor;
  }

  function fromBytes(bytes memory data) internal pure returns (CBORBuffer memory) {
    Buffer.buffer memory buf;
    Buffer.init(buf, data);
    CBORBuffer memory cbor = CBORBuffer(buf, 0);
    return cbor;
  }

  function readBytes32(CBORBuffer memory self) internal pure returns (bytes32) {
    // Simplified - just return a default value for now
    return bytes32(0);
  }

  function readAddress(CBORBuffer memory self) internal pure returns (address) {
    // Simplified - just return a default value for now
    return address(0);
  }

  function readBytes(CBORBuffer memory self) internal pure returns (bytes memory) {
    // Simplified - just return empty bytes for now
    return "";
  }

  function readUint(CBORBuffer memory self) internal pure returns (uint256) {
    // Simplified - just return 0 for now
    return 0;
  }
}

/// @title A buffer for storing a growing bytes array
/// @notice A buffer with a minimum capacity of 1024 bytes
/// @dev The buffer is not thread safe and should not be accessed concurrently
library Buffer {
  /// @notice Represents a growing bytes array
  /// @dev The buffer is not thread safe and should not be accessed concurrently
  struct buffer {
    bytes b;
  }

  /// @notice Initializes a new buffer with a minimum capacity
  /// @param buf The buffer to initialize
  /// @param capacity The minimum capacity of the buffer
  function init(buffer memory buf, uint256 capacity) internal pure {
    if (capacity == 0) {
      buf.b = new bytes(0);
    } else {
      buf.b = new bytes(capacity);
    }
  }

  /// @notice Initializes a new buffer with an initial bytes array
  /// @param buf The buffer to initialize
  /// @param b The initial bytes array
  function init(buffer memory buf, bytes memory b) internal pure {
    buf.b = b;
  }

  /// @notice Resizes the buffer to fit at least one more byte
  /// @param buf The buffer to resize
  /// @param newCapacity The new capacity of the buffer
  function resize(buffer memory buf, uint256 newCapacity) internal pure {
    bytes memory newb = new bytes(newCapacity);
    uint256 len = buf.b.length;
    if (len > 0) {
      _memcpy(newb, 0, buf.b, 0, len);
    }
    buf.b = newb;
  }

  /// @notice Appends data to the end of the buffer
  /// @param buf The buffer to append to
  /// @param data The data to append
  /// @param len The length of the data to append
  function append(buffer memory buf, bytes memory data, uint256 len) internal pure {
    if (len > 0) {
      uint256 newCapacity = buf.b.length + len;
      if (newCapacity > buf.b.length) {
        resize(buf, newCapacity);
      }
      _memcpy(buf.b, buf.b.length, data, 0, len);
    }
  }

  /// @notice Appends a byte to the end of the buffer
  /// @param buf The buffer to append to
  /// @param data The byte to append
  function append(buffer memory buf, uint8 data) internal pure {
    uint256 off = buf.b.length;
    uint256 offPlusOne = off + 1;
    if (off >= buf.b.length) {
      resize(buf, offPlusOne);
    }
    buf.b[off] = bytes1(data);
  }

  /// @notice Appends a bytes32 to the end of the buffer
  /// @param buf The buffer to append to
  /// @param data The bytes32 to append
  function append(buffer memory buf, bytes32 data) internal pure {
    uint256 off = buf.b.length;
    uint256 offPlusOne = off + 32;
    if (off >= buf.b.length) {
      resize(buf, offPlusOne);
    }
    _memcpy(buf.b, off, data, 0, 32);
  }

  /// @notice Appends an address to the end of the buffer
  /// @param buf The buffer to append to
  /// @param data The address to append
  function append(buffer memory buf, address data) internal pure {
    uint256 off = buf.b.length;
    uint256 offPlusOne = off + 20;
    if (off >= buf.b.length) {
      resize(buf, offPlusOne);
    }
    _memcpy(buf.b, off, data, 0, 20);
  }

  /// @notice Appends a uint256 to the end of the buffer
  /// @param buf The buffer to append to
  /// @param data The uint256 to append
  function append(buffer memory buf, uint256 data) internal pure {
    uint256 off = buf.b.length;
    uint256 offPlusOne = off + 32;
    if (off >= buf.b.length) {
      resize(buf, offPlusOne);
    }
    _memcpy(buf.b, off, data, 0, 32);
  }

  /// @notice Appends a string to the end of the buffer
  /// @param buf The buffer to append to
  /// @param data The string to append
  function append(buffer memory buf, string memory data) internal pure {
    append(buf, bytes(data));
  }

  /// @notice Appends bytes to the end of the buffer
  /// @param buf The buffer to append to
  /// @param data The bytes to append
  function append(buffer memory buf, bytes memory data) internal pure {
    append(buf, data, data.length);
  }

  /// @notice Appends a string to the end of the buffer (for encoding)
  /// @param buf The buffer to append to
  /// @param data The string to append
  function encodeString(buffer memory buf, string memory data) internal pure {
    append(buf, bytes(data));
  }

  /// @notice Appends bytes to the end of the buffer (for encoding)
  /// @param buf The buffer to append to
  /// @param data The bytes to append
  function encodeBytes(buffer memory buf, bytes memory data) internal pure {
    append(buf, data);
  }

  /// @notice Appends a uint256 to the end of the buffer (for encoding)
  /// @param buf The buffer to append to
  /// @param data The uint256 to append
  function encodeUInt(buffer memory buf, uint256 data) internal pure {
    append(buf, data);
  }

  /// @notice Appends an array of strings to the end of the buffer (for encoding)
  /// @param buf The buffer to append to
  /// @param data The array of strings to append
  function encodeArray(buffer memory buf, string[] memory data) internal pure {
    for (uint256 i = 0; i < data.length; i++) {
      append(buf, bytes(data[i]));
    }
  }

  /// @notice Appends an address to the end of the buffer (for encoding)
  /// @param buf The buffer to append to
  /// @param data The address to append
  function encodeAddress(buffer memory buf, address data) internal pure {
    append(buf, data);
  }

  /// @notice Writes a byte to the buffer at a specific offset
  /// @param buf The buffer to write to
  /// @param off The offset to write at
  /// @param data The byte to write
  function write(buffer memory buf, uint256 off, uint8 data) internal pure {
    if (off >= buf.b.length) {
      resize(buf, off + 1);
    }
    buf.b[off] = bytes1(data);
  }

  /// @notice Writes a bytes32 to the buffer at a specific offset
  /// @param buf The buffer to write to
  /// @param off The offset to write at
  /// @param data The bytes32 to write
  function write(buffer memory buf, uint256 off, bytes32 data) internal pure {
    if (off + 32 > buf.b.length) {
      resize(buf, off + 32);
    }
    _memcpy(buf.b, off, data, 0, 32);
  }

  /// @notice Writes an address to the buffer at a specific offset
  /// @param buf The buffer to write to
  /// @param off The offset to write at
  /// @param data The address to write
  function write(buffer memory buf, uint256 off, address data) internal pure {
    if (off + 20 > buf.b.length) {
      resize(buf, off + 20);
    }
    _memcpy(buf.b, off, data, 0, 20);
  }

  /// @notice Writes a uint256 to the buffer at a specific offset
  /// @param buf The buffer to write to
  /// @param off The offset to write at
  /// @param data The uint256 to write
  function write(buffer memory buf, uint256 off, uint256 data) internal pure {
    if (off + 32 > buf.b.length) {
      resize(buf, off + 32);
    }
    _memcpy(buf.b, off, data, 0, 32);
  }

  /// @notice Writes a string to the buffer at a specific offset
  /// @param buf The buffer to write to
  /// @param off The offset to write at
  /// @param data The string to write
  function write(buffer memory buf, uint256 off, string memory data) internal pure {
    write(buf, off, bytes(data));
  }

  /// @notice Writes bytes to the buffer at a specific offset
  /// @param buf The buffer to write to
  /// @param off The offset to write at
  /// @param data The bytes to write
  function write(buffer memory buf, uint256 off, bytes memory data) internal pure {
    if (off + data.length > buf.b.length) {
      resize(buf, off + data.length);
    }
    _memcpy(buf.b, off, data, 0, data.length);
  }

  /// @notice Copies memory from one location to another
  /// @param dest The destination memory location
  /// @param destOff The destination offset
  /// @param src The source memory location
  /// @param srcOff The source offset
  /// @param len The length of the data to copy
  function _memcpy(
    bytes memory dest,
    uint256 destOff,
    bytes memory src,
    uint256 srcOff,
    uint256 len
  ) private pure {
    if (len == 0) return;

    for (uint256 i = 0; i < len; i++) {
      dest[destOff + i] = src[srcOff + i];
    }
  }

  /// @notice Copies memory from one location to another
  /// @param dest The destination memory location
  /// @param destOff The destination offset
  /// @param src The source memory location
  /// @param srcOff The source offset
  /// @param len The length of the data to copy
  function _memcpy(
    bytes memory dest,
    uint256 destOff,
    bytes32 src,
    uint256 srcOff,
    uint256 len
  ) private pure {
    if (len == 0) return;

    for (uint256 i = 0; i < len; i++) {
      dest[destOff + i] = src[srcOff + i];
    }
  }

  /// @notice Copies memory from one location to another
  /// @param dest The destination memory location
  /// @param destOff The destination offset
  /// @param src The source memory location
  /// @param srcOff The source offset
  /// @param len The length of the data to copy
  function _memcpy(
    bytes memory dest,
    uint256 destOff,
    address src,
    uint256 srcOff,
    uint256 len
  ) private pure {
    if (len == 0) return;

    for (uint256 i = 0; i < len; i++) {
      dest[destOff + i] = bytes20(src)[srcOff + i];
    }
  }

  /// @notice Copies memory from one location to another
  /// @param dest The destination memory location
  /// @param destOff The destination offset
  /// @param src The source memory location
  /// @param srcOff The source offset
  /// @param len The length of the data to copy
  function _memcpy(
    bytes memory dest,
    uint256 destOff,
    uint256 src,
    uint256 srcOff,
    uint256 len
  ) private pure {
    if (len == 0) return;

    for (uint256 i = 0; i < len; i++) {
      dest[destOff + i] = bytes32(src)[srcOff + i];
    }
  }
}

contracts/chainlink/functions/FunctionsC.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Address.sol";

import "./FunctionsRequest.sol";
import "./FunctionsResponse.sol";

/// @title The Chainlink Functions client contract
/// @notice Contract developers can inherit this contract to easily create Chainlink Functions requests
abstract contract FunctionsClient is ReentrancyGuard {
  using FunctionsRequest for FunctionsRequest.Request;
  using FunctionsResponse for FunctionsResponse.Response;

  error SenderIsNotRegistry();
  error RequestIsAlreadyPending();
  error RequestIsNotPending();
  error RequestNotFound();
  error InvalidCallbackFunction();
  error ResponseIsEmpty();
  error NoErrorInResponse();
  error CostExceedsAvailableFunds();

  event RequestSent(bytes32 indexed id);
  event RequestFulfilled(bytes32 indexed id);

  struct RequestCommitment {
    address coordinator;
    address client;
    uint64 subscriptionId;
    uint32 callbackGasLimit;
    uint256 estimatedTotalCostJuels;
    address sender;
    address don;
    uint96 adminFee;
    uint64 timeoutTimestamp;
  }

  mapping(bytes32 => RequestCommitment) private s_pendingRequests;

  // Contains information about a request that was initiated to the DON. Used for reentrancy protection.
  mapping(bytes32 => bool) private s_commitments;

  /// @dev Ensures that the fulfillment is valid for this contract
  modifier onlyValidRequest(bytes32 requestId) {
    if (s_pendingRequests[requestId].client == address(0)) {
      revert RequestNotFound();
    }
    if (msg.sender != s_pendingRequests[requestId].coordinator) {
      revert SenderIsNotRegistry();
    }
    _;
    delete s_pendingRequests[requestId];
    delete s_commitments[requestId];
  }

  /// @dev Ensures that the request is not already pending
  modifier notPendingRequest(bytes32 requestId) {
    if (s_commitments[requestId]) {
      revert RequestIsAlreadyPending();
    }
    _;
  }

  constructor() {}

  /// @notice Sends a Chainlink Functions request
  /// @param req The initialized Functions.Request
  /// @param subscriptionId The subscription ID
  /// @param callbackGasLimit Gas limit for the fulfillment callback
  /// @return requestId The generated request ID
  function _sendRequest(
    FunctionsRequest.Request memory req,
    uint64 subscriptionId,
    uint32 callbackGasLimit,
    uint256 gasPrice
  ) internal returns (bytes32) {
    bytes32 requestId = keccak256(abi.encode(msg.sender, block.number, req));
    
    s_commitments[requestId] = true;
    
    // In a real implementation, this would call the Functions Router
    // For now, we'll just emit the event
    emit RequestSent(requestId);
    
    return requestId;
  }

  /// @notice User defined function to handle a response
  /// @param requestId The request ID for fulfillment
  /// @param response The CBOR encoded response
  /// @param err Any errors from the Functions request
  function _fulfillRequest(
    bytes32 requestId,
    bytes memory response,
    bytes memory err
  ) internal virtual {
    emit RequestFulfilled(requestId);
    
    // Call the callback function
    if (response.length > 0) {
      _handleResponse(requestId, response);
    } else if (err.length > 0) {
      _handleError(requestId, err);
    } else {
      revert ResponseIsEmpty();
    }
  }

  /// @notice User defined function to handle a response from Chainlink Functions
  /// @param requestId The request ID for fulfillment
  /// @param response The CBOR encoded response
  function _handleResponse(bytes32 requestId, bytes memory response) internal virtual {}

  /// @notice User defined function to handle an error response from Chainlink Functions
  /// @param requestId The request ID for fulfillment
  /// @param err The CBOR encoded error
  function _handleError(bytes32 requestId, bytes memory err) internal virtual {}
}

