// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "./FunctionsResponse.sol";
import "./CBOR.sol";

/// @title Library for encoding/decoding CBOR for Chainlink Functions
/// @notice https://www.rfc-editor.org/rfc/rfc7049.html
library FunctionsRequest {
  using Buffer for Buffer.buffer;

  uint16 public constant REQUEST_DATA_VERSION = 1;
  uint256 internal constant DEFAULT_BUFFER_SIZE = 256;

  enum Location {
    Inline, // Provided within the Request
    Remote, // Hosted through remote location that can be accessed through a provided URL
    DONHosted // Hosted on the DON's storage
  }

  enum CodeLanguage {
    JavaScript
  }

  struct Request {
    bytes32 codeLocation; // ═══════════════════════════════════════════════════════════════════════════════════════
    Location location; // ═══════════════════════════════════════════════════════════════════════════════════════
    CodeLanguage language; // ═══════════════════════════════════════════════════════════════════════════════════════
    string source; // ═══════════════════════════════════════════════════════════════════════════════════════
    bytes secrets; // ═══════════════════════════════════════════════════════════════════════════════════════
    string[] args; // ═══════════════════════════════════════════════════════════════════════════════════════
  }

  error EmptySource();
  error EmptySecrets();
  error EmptyArgs();
  error NoInlineSecrets();

  /// @notice Encodes a Request to CBOR encoded bytes
  /// @param self The request to encode
  /// @return CBOR encoded bytes
  function encodeCBOR(Request memory self) internal pure returns (bytes memory) {
    Buffer.buffer memory buf;
    Buffer.init(buf, DEFAULT_BUFFER_SIZE);

    buf.encodeString("codeLocation");
    buf.encodeBytes(abi.encodePacked(self.codeLocation));

    buf.encodeString("location");
    buf.encodeUInt(uint256(self.location));

    buf.encodeString("language");
    buf.encodeUInt(uint256(self.language));

    buf.encodeString("source");
    buf.encodeString(self.source);

    if (self.secrets.length > 0) {
      buf.encodeString("secretsLocation");
      buf.encodeUInt(uint256(Location.DONHosted));
      buf.encodeString("secrets");
      buf.encodeBytes(self.secrets);
    }

    if (self.args.length > 0) {
      buf.encodeString("args");
      buf.encodeArray(self.args);
    }

    return buf.b;
  }

  /// @notice Initializes a Chainlink Functions Request
  /// @dev Sets the codeLocation and code on the request
  /// @param self The uninitialized request
  /// @param location The user provided host and path to the user code
  /// @param language The programming language of the user code
  /// @param source The source code for the user code
  function initializeRequest(
    Request memory self,
    Location location,
    CodeLanguage language,
    string memory source
  ) internal pure {
    if (bytes(source).length == 0) revert EmptySource();

    self.codeLocation = bytes32(0);
    self.location = location;
    self.language = language;
    self.source = source;
  }

  /// @notice Sets the secrets for the request
  /// @param self The initialized request
  /// @param secrets The CBOR encoded secrets
  function addInlineSecrets(Request memory self, bytes memory secrets) internal pure {
    if (secrets.length == 0) revert EmptySecrets();

    self.secrets = secrets;
  }

  /// @notice Sets the args for the request
  /// @param self The initialized request
  /// @param args The array of string args (must not be empty)
  function addArgs(Request memory self, string[] memory args) internal pure {
    if (args.length == 0) revert EmptyArgs();

    self.args = args;
  }
}

// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "./FunctionsClient.sol";
import "./CBOR.sol";

/// @title Library for encoding/decoding CBOR for Chainlink Functions
/// @notice https://www.rfc-editor.org/rfc/rfc7049.html
library FunctionsResponse {
  using Buffer for Buffer.buffer;
  using CBOR for CBOR.CBORBuffer;

  uint16 public constant RESPONSE_DATA_VERSION = 1;
  uint256 internal constant DEFAULT_BUFFER_SIZE = 256;

  enum ErrorCode {
    None,
    InvalidRequest, // The request is invalid and can not be processed
    InternalError, // The request handler encountered an internal error
    ExternalError, // The request handler encountered an external error (e.g. API error)
    InsufficientGas // The request handler ran out of gas during execution
  }

  struct Response {
    bytes32 requestId; // ═══════════════════════════════════════════════════════════════════════════════════════
    address requester; // ═══════════════════════════════════════════════════════════════════════════════════════
    bytes data; // ═══════════════════════════════════════════════════════════════════════════════════════
    uint8 erc; // ═══════════════════════════════════════════════════════════════════════════════════════
    uint256 gasUsed; // ═══════════════════════════════════════════════════════════════════════════════════════
    uint256 gasLimit; // ═══════════════════════════════════════════════════════════════════════════════════════
    uint256 cost; // ═══════════════════════════════════════════════════════════════════════════════════════
    address don; // ═══════════════════════════════════════════════════════════════════════════════════════
  }

  /// @notice Encodes a Response to CBOR encoded bytes
  /// @param self The response to encode
  /// @return CBOR encoded bytes
  function encodeCBOR(Response memory self) internal pure returns (bytes memory) {
    Buffer.buffer memory buf;
    Buffer.init(buf, DEFAULT_BUFFER_SIZE);

    buf.encodeString("requestId");
    buf.encodeBytes(abi.encodePacked(self.requestId));

    buf.encodeString("requester");
    buf.encodeAddress(self.requester);

    buf.encodeString("data");
    buf.encodeBytes(self.data);

    buf.encodeString("erc");
    buf.encodeUInt(self.erc);

    buf.encodeString("gasUsed");
    buf.encodeUInt(self.gasUsed);

    buf.encodeString("gasLimit");
    buf.encodeUInt(self.gasLimit);

    buf.encodeString("cost");
    buf.encodeUInt(self.cost);

    buf.encodeString("don");
    buf.encodeAddress(self.don);

    return buf.b;
  }

  /// @notice Decodes a CBOR encoded Response
  /// @param data The CBOR encoded bytes
  /// @return The decoded Response
  function decodeCBOR(bytes memory data) internal pure returns (Response memory) {
    CBOR.CBORBuffer memory cbor = CBOR.fromBytes(data);

    return Response({
      requestId: cbor.readBytes32(),
      requester: cbor.readAddress(),
      data: cbor.readBytes(),
      erc: uint8(cbor.readUint()),
      gasUsed: cbor.readUint(),
      gasLimit: cbor.readUint(),
      cost: cbor.readUint(),
      don: cbor.readAddress()
    });
  }
}

